mapper.update:
UPDATE tb_category_task
SET sort_order = CASE

    WHEN category_id = #{item.categoryId} AND task_id = #{item.taskId} THEN #{item.sortOrder}

END
WHERE (

    category_id = #{item.categoryId} AND task_id = #{item.taskId}

)

mapper.select:
SELECT *
FROM tb_category_task
WHERE category_id = #{categoryId}
AND task_id IN

    #{taskId}
